import{_ as t,c as o,o as a,l as e,a as s}from"./chunks/framework.TD5rc-0R.js";const $=JSON.parse('{"title":"送鞭子","description":"","frontmatter":{},"headers":[],"relativePath":"article/storybook/10.md","filePath":"article/storybook/10.md","lastUpdated":1716972082000}'),r={name:"article/storybook/10.md"},n=e("h1",{id:"送鞭子",tabindex:"-1"},[s("送鞭子 "),e("a",{class:"header-anchor",href:"#送鞭子","aria-label":'Permalink to "送鞭子"'},"​")],-1),c=e("p",null,"这是一个真实的故事。",-1),l=e("p",null,"我还很小的时候，大概上二三年级的一个周末。",-1),_=e("p",null,"奶奶和我没事儿闲聊天地说：“还记得你之前自己一个人都跑到桥空子去给你爷送鞭子吗？那时候咱家还养着一堆羊，你爷每天还都得去北山放羊去。那天你正在门口玩呢，我在屋里叫你，叫半天没听见你答应，我就去了邻居家找，又去了下坎儿找，叫了半天，找了半天也找不着人。哎呦喂，给我急坏了，哪也找不到人，走在庄里看到一个问一个人，有没有看见我家 xx，都没人看到。我就往桥空子方向走，碰到了 xxx，我就问有没有看见我家 xx，他说：‘哎！我还真看着一个小孩儿，往桥空子那边踮儿了个踮儿的跑去了。’，我就赶紧往那边追，等我追到你，你都已经跑到桥空子地下了，手里还拿着鞭子。我问你干啥来了，你还和我说：‘爷放羊没拿鞭子，我给他送鞭子来了！’。然后我就给你带家来了。过会功夫你爷回来了，我就和他说：‘你孙子说你放羊没拿鞭子，要给你送鞭子去了，都跑到桥空子了，这要是让人抱走可咋整！’。”。",-1),d=e("p",null,"这个故事奶奶和我说了好几次，实在是印象深刻，现在将它记录在这里。",-1),i=[n,c,l,_,d];function p(h,x,m,f,u,k){return a(),o("div",null,i)}const y=t(r,[["render",p]]);export{$ as __pageData,y as default};
