import{_ as a,c as e,o as t,aq as o}from"./chunks/framework.Cg19dBxK.js";const b=JSON.parse('{"title":"词性类型","description":"","frontmatter":{},"headers":[],"relativePath":"docs/english/3.md","filePath":"docs/english/3.md","lastUpdated":1713326588000}'),r={name:"docs/english/3.md"},h=o('<h1 id="词性类型" tabindex="-1">词性类型 <a class="header-anchor" href="#词性类型" aria-label="Permalink to &quot;词性类型&quot;">​</a></h1><h2 id="名词" tabindex="-1">名词 <a class="header-anchor" href="#名词" aria-label="Permalink to &quot;名词&quot;">​</a></h2><p>表示人或者事物的名称。</p><p>cup、BeiJing、coffee、computer</p><h2 id="冠词" tabindex="-1">冠词 <a class="header-anchor" href="#冠词" aria-label="Permalink to &quot;冠词&quot;">​</a></h2><p>用于名词前，帮助说明名词所指的人或者事物。</p><p>the、a、an</p><h2 id="代词" tabindex="-1">代词 <a class="header-anchor" href="#代词" aria-label="Permalink to &quot;代词&quot;">​</a></h2><p>用于代替名词，以及起名词作用的短语。</p><p>me、that、what、you、your、he、she、it</p><h2 id="动词" tabindex="-1">动词 <a class="header-anchor" href="#动词" aria-label="Permalink to &quot;动词&quot;">​</a></h2><p>表示动作或者状态。</p><p>run、junmp、drink、watch、stand（站着）</p><h2 id="形容词" tabindex="-1">形容词 <a class="header-anchor" href="#形容词" aria-label="Permalink to &quot;形容词&quot;">​</a></h2><p>描写或者修饰名词或者代词，表示人或者事物的性质。</p><p>happy、handsome（帅气的）、nice、long、short</p><h2 id="副词" tabindex="-1">副词 <a class="header-anchor" href="#副词" aria-label="Permalink to &quot;副词&quot;">​</a></h2><p>修饰动词、形容词，表示地点、时间、方式、频率等。</p><p>very（非常地）、happily（高兴地）、suddenly（突然的）</p><h2 id="连词" tabindex="-1">连词 <a class="header-anchor" href="#连词" aria-label="Permalink to &quot;连词&quot;">​</a></h2><p>连接词与词、句与句、短语与短语。</p><p>so、but、and、or、because、yet（也）</p><h2 id="介词" tabindex="-1">介词 <a class="header-anchor" href="#介词" aria-label="Permalink to &quot;介词&quot;">​</a></h2><p>表示名词、代词语句中其它词的关系。</p><p>in、on、at、over、about、for、with、by</p><h2 id="感叹词" tabindex="-1">感叹词 <a class="header-anchor" href="#感叹词" aria-label="Permalink to &quot;感叹词&quot;">​</a></h2><p>表示说话时候的高兴、惊叹的情感。</p><p>wow、oh、hahaha</p><h2 id="数词" tabindex="-1">数词 <a class="header-anchor" href="#数词" aria-label="Permalink to &quot;数词&quot;">​</a></h2><p>表示数量或者顺序。</p><p>one、two、first、second</p>',31),i=[h];function n(l,p,s,d,c,u){return t(),e("div",null,i)}const m=a(r,[["render",n]]);export{b as __pageData,m as default};
